/* Custom scrollbar for seat map */
#seat-map::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}
#seat-map::-webkit-scrollbar-thumb {
    background-color: #6366f1; /* Indigo 600 */
    border-radius: 4px;
}
#seat-map::-webkit-scrollbar-track {
    background-color: #e0e7ff; /* Indigo 100 */
}

/* Seat styles beyond Tailwind for shape and cursor */
.seat {
    aspect-ratio: 1 / 1;
    border-radius: 0.35rem;
    cursor: pointer;
    transition: background-color 0.25s ease, transform 0.2s ease;
}
.seat:hover:not(.occupied):not(.selected) {
    transform: scale(1.1);
    background-color: #4338ca; /* Indigo 700 */
}
.seat.occupied {
    cursor: not-allowed;
    background-color: #ef4444; /* Red 500 */
}
.seat.selected {
    background-color: #4f46e5; /* Indigo 600 */
}

document.addEventListener('DOMContentLoaded', () => {
    const form = document.getElementById('flight-form');

    form.addEventListener('submit', (e) => {
        e.preventDefault();

        const from = form.from.value;
        const to = form.to.value;
        const date = form.date.value;
        const passengers = parseInt(form.passengers.value, 10);

        if (from === to) {
            alert('Departure and destination cannot be the same.');
            return;
        }

        // Save flight details to sessionStorage for next page
        const flightDetails = { from, to, date, passengers };
        sessionStorage.setItem('flightDetails', JSON.stringify(flightDetails));

        // Redirect to seat selection page
        window.location.href = 'seat-selection.html';
    });
});

document.addEventListener('DOMContentLoaded', () => {
    const seatMapContainer = document.getElementById('seat-map');
    const flightSummary = document.getElementById('flight-summary');
    const confirmBtn = document.getElementById('confirm-btn');

    // Load flight details from sessionStorage
    const flightDetails = JSON.parse(sessionStorage.getItem('flightDetails')) || null;

    if (!flightDetails) {
        alert('No flight details found. Redirecting to home.');
        window.location.href = 'index.html';
        return;
    }

    // Display flight summary
    flightSummary.innerHTML = `<p><strong>From:</strong> ${flightDetails.from}</p><p><strong>To:</strong> ${flightDetails.to}</p><p><strong>Date:</strong> ${flightDetails.date}</p><p><strong>Passengers:</strong> ${flightDetails.passengers}</p>`;

    // Seat map configuration
    const rows = 10;
    const cols = 7; // 7 seats per row
    const totalSeats = rows * cols;

    // Randomly mark some seats as occupied
    const occupiedSeats = new Set();
    while (occupiedSeats.size < Math.floor(totalSeats * 0.15)) { // 15% seats occupied
        occupiedSeats.add(Math.floor(Math.random() * totalSeats));
    }

    // Track selected seats
    const selectedSeats = new Set();

    // Generate seat labels (A-G)
    const seatLetters = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];

    // Render seats
    for (let i = 0; i < totalSeats; i++) {
        const seat = document.createElement('div');
        seat.classList.add('seat');

        const row = Math.floor(i / cols) + 1;
        const col = i % cols;
        const seatLabel = `${row}${seatLetters[col]}`;

        seat.textContent = seatLabel;

        if (occupiedSeats.has(i)) {
            seat.classList.add('occupied');
        }

        seat.addEventListener('click', () => {
            if (seat.classList.contains('occupied')) return;

            if (seat.classList.contains('selected')) {
                seat.classList.remove('selected');
                selectedSeats.delete(seatLabel);
            } else {
                if (selectedSeats.size < flightDetails.passengers) {
                    seat.classList.add('selected');
                    selectedSeats.add(seatLabel);
                } else {
                    alert(`You can only select up to ${flightDetails.passengers} seat(s).`);
                }
            }
        });

        seatMapContainer.appendChild(seat);
    }

    // Confirm button handler
    confirmBtn.addEventListener('click', () => {
        if (selectedSeats.size !== flightDetails.passengers) {
            alert(`Please select exactly ${flightDetails.passengers} seat(s) before proceeding.`);
            return;
        }

        // Save selected seats
        sessionStorage.setItem('selectedSeats', JSON.stringify(Array.from(selectedSeats)));

        // Redirect to confirmation page
        window.location.href = 'confirmation.html';
    });
});

document.addEventListener('DOMContentLoaded', () => {
    const bookingDetailsDiv = document.getElementById('booking-details');

    const flightDetails = JSON.parse(sessionStorage.getItem('flightDetails')) || null;
    const selectedSeats = JSON.parse(sessionStorage.getItem('selectedSeats')) || null;

    if (!flightDetails || !selectedSeats) {
        alert('Booking information incomplete. Redirecting to home.');
        window.location.href = 'index.html';
        return;
    }

    const detailsHTML = `
        <p><strong>From:</strong> ${flightDetails.from}</p>
        <p><strong>To:</strong> ${flightDetails.to}</p>
        <p><strong>Date:</strong> ${flightDetails.date}</p>
        <p><strong>Passengers:</strong> ${flightDetails.passengers}</p>
        <p><strong>Selected Seat(s):</strong> ${selectedSeats.join(', ')}</p>
    `;

    bookingDetailsDiv.innerHTML = detailsHTML;
});
